import AceEditor from "react-ace";
import Sidebar from './components/Sidebar/Sidebar';
import NavbarRec from './components/NavbarRec/NavbarRec';
const Record = (props) => {
    return(
        <div>
            <NavbarRec recordHandler={props.recordHandler} />
            <div style={{ display: 'inline-flex', height: '100%' , width: '100%', backgroundColor:'#282c34'}}>
              <Sidebar textInIDE={props.textInIDE} setTextInIDE={props.setTextInIDE}/>
              <AceEditor
              mode="javascript"
              theme="one_dark"
              height='100vh'
              width='200vh'
              onChange={props.codeValue}
              value={props.textInIDE}
              defaultValue= {props.defaultVal}
              fontSize= {26}
              setOptions={{
                enableBasicAutocompletion: true,
                enableLiveAutocompletion: true,
                enableSnippets: true,
              }}  
              
              name="UNIQUE_ID_OF_DIV"
              editorProps={{ $blockScrolling: true }}
            />
          </div>
        </div>
    )
}

export default Record;
