import './App.css';
import AceEditor from "react-ace";
import Sidebar from './components/Sidebar/Sidebar';
import Navbar from './components/Navbar/Navbar';
import { useState } from 'react';
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/theme-one_dark";
import "ace-builds/src-noconflict/ext-language_tools";
let defaultVal = `/* This is Eliminating Silly Switching Online IDE.
* You can start coding & we will record the session for you!
*/
console.log('Hello world, from ESS');`

function App() {
  const [textInIDE, setTextInIDE] = useState(defaultVal);
  const [recordText, setRecordText] = useState(defaultVal);
  const [record, setRecord] = useState(false);
  let userActions = [];
  let counter = 0;

  const sendData = async (data) => {
    const result = await fetch('http://localhost:5000/record', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    }
    )
    console.log(result);
  }
  const playHandler = () => {
    setRecordText(recordText);
    let lines = recordText.split('\n');
    const playRecursive = () => {
      setTimeout(()=>{
        counter--;
        lines[userActions[counter]['start'].row] = lines[userActions[counter]['start'].row].slice(0,userActions[counter]['start'].column) + userActions[counter]['lines'] + lines[userActions[counter]['start'].row].slice(userActions[counter]['start'].column);
        lines = lines.join('\n');
        setTextInIDE(lines);
        lines = lines.split('\n')
        if(counter !== 0)
          playRecursive();
      },userActions[counter-1].timestamp - userActions[counter].timestamp);
    }
    playRecursive();
  }

  const recordHandler = () => {
    setRecordText(textInIDE);
    setRecord(true);
    const timestamp = Date.now();
    userActions.unshift({action: '1', lines: '2', start: '3', timestamp: timestamp})
    console.log(userActions);
  }

  const codeValue= (value,event) => {
    if(record === true)
    {
      const timestamp = Date.now();
      let currentEvent = {action: event.action, lines: event.lines, start: event.start, timestamp: timestamp};
      sendData(currentEvent);
      userActions.unshift(currentEvent);

      counter++;
    }
    else
      console.log(userActions)
  }
  return (
    <div>
      <Navbar playHandler={playHandler} recordHandler={recordHandler} />
      <div style={{ display: 'inline-flex', height: '100%' , width: '100%', backgroundColor:'#282c34'}}>
        <Sidebar textInIDE={textInIDE} setTextInIDE={setTextInIDE}/>
        <AceEditor
        mode="javascript"
        theme="one_dark"
        height='100vh'
        width='200vh'
        onChange={codeValue}
        value={textInIDE}
        defaultValue= {defaultVal}
        fontSize= {26}
        setOptions={{
          enableBasicAutocompletion: true,
          enableLiveAutocompletion: true,
          enableSnippets: true,
        }}  

        name="UNIQUE_ID_OF_DIV"
        editorProps={{ $blockScrolling: true }}
      />
    </div>
  </div>
  );
}

export default App;
